import React, { Fragment } from 'react';
import PropTypes from 'prop-types';

const ProfileAbout = ({
  profile: {
    bio,
    skills,
    user: { name },
  },
}) => (
  <div class='profile-about bg-light p-2'>
    {bio && (
      <Fragment>
        {/* In this way, "Yu Wang" is an array, "Yu" is the first item and "Wang" is the second */}
        <h2 class='text-primary'>{name.trim().split(' ')[0]}'s Bio</h2>
        <p>{bio}</p>
      </Fragment>
    )}

    <div class='line'></div>
    <h2 class='text-primary'>Skill Set</h2>
    <div class='skills'>
      <div class='p-1'>
        <i class='fa fa-check'></i> HTML
      </div>
      <div class='p-1'>
        <i class='fa fa-check'></i> CSS
      </div>
      <div class='p-1'>
        <i class='fa fa-check'></i> JavaScript
      </div>
      <div class='p-1'>
        <i class='fa fa-check'></i> Python
      </div>
      <div class='p-1'>
        <i class='fa fa-check'></i> C#
      </div>
    </div>
  </div>
);

ProfileAbout.propTypes = {
  profile: PropTypes.object.isRequired,
};

export default ProfileAbout;
